var second = ['turned highly creative','self-taught',' front-end developer','and Tech enthusiast',];
var secondM = [], el;


var $second = $('.the-second');


// init static //

$second.text(second[0]);

// create measurables //

for(var i = 0; i < second.length; i++){
    el = $('<div class="measurable">' + second[i] + '</div>');

    secondM.push(el.width());
}

// absolutize //


// remember initial sizes //

var secondInitialWidth = $second.width();

// loop the loop //
var activeWordsIndex = 0;
setInterval(function(){
    activeWordsIndex++;

    var secondIndex = activeWordsIndex % second.length;


    $second.text( second[secondIndex] );


    var secondLineOffset = (secondM[secondIndex] - secondInitialWidth) / 2;


    $('.static.second').css({
        transform: 'translateX(' + (-secondLineOffset) + 'px)'
    });




    $second.css({
        transition: 'none',
        transform: 'translate(' + (-secondLineOffset) + 'px, 30px)',
        opacity: '0'
    });
    setTimeout(function(){
        $second.css({
            transition: 'all 1s ease',
            transform: 'translate(' + (-secondLineOffset) + 'px, 0px)',
            opacity: '1'
        });
    }, 50);
}, 2000);

// jQuery skills, socialIcons and pdf images animation
$(document).ready(function(){
    $('.skills table tr td img , .socialIcons img, #pdfImg').hover(function() {
        $(this).addClass('transition');

    }, function() {
        $(".skills table tr td img, .socialIcons img, #pdfImg").removeClass('transition');
    });
});


